import { Component } from '@angular/core';
import { Article } from '../models/article';
import { AjoutArticleComponent } from "../ajout-article/ajout-article.component";

@Component({
  selector: 'app-articles-list',
  standalone: true,
  imports: [AjoutArticleComponent],
  templateUrl: './articles-list.component.html',
  styleUrl: './articles-list.component.css'
})
export class ArticlesListComponent {
  title = "Liste des nouveaux articles :";
   Articles = [
    new Article(1,'Vogue mode', 'data:image/jpeg;base64,', '14/03/2014', 'Le meilleur des tendances et de la mode, l intégralité des défilés de New York, Londres, Milan et Paris, le tout édité par la rédaction de Vogue fr Un décryptage des trends, sans oublier des rencontres avec des insiders, toute l actualité de la planète fashion, nos astuces de style et des shoppings à la pointe pour capter l air du temps et en mettre plein la vue.',0),
    new Article(2,'Elle magazine', 'data:image/jpeg;base64,','15/11/2023', 'ELLE est un des titres phares de la presse magazine féminine, aujourd hui décliné dans plus de 60 pays. Créé après la Seconde Guerre mondiale, ELLE devient, dans les années 1980, une marque déployée sur le papier et, un peu plus tard, sur le numérique, relayée par des applications et une plateforme vidéo.',0),
    new Article(3,'Hia magazine ', 'data:image/png;base64,', '20/08/2022', 'Hia est un magazine mensuel féminin arabe centré sur les femmes et les femmes d affaires du monde arabe.',0),
  ];
  action = "";

  changeAction(action : string){
    this.action = action;
  }
  addArticle(Article:Article){
    this.Articles=[...this.Articles,Article];
    this.changeAction("");
  }
  addscore(Article :Article){
  Article.score++;
  }
  removescore(Article :Article){
    if (Article.score > 0) {
      Article.score--; 
  }
}
  TriArticles() {
  return this.Articles.sort((article1,article2) => article2.score - article1.score);
}
 filtrerArticle(titre :string):void{
      this.Articles = this.Articles.filter(b => b.titre.toLowerCase() ==titre.toLowerCase());
    }
}
